dividerColor:
{{
  function() {
    if (appsmith.store.selectedPredictionData.actual_result === 'won') {
      return 'GREEN';
    } else if (appsmith.store.selectedPredictionData.actual_result === 'lost') {
      return 'RED';
    } else if (appsmith.store.selectedPredictionData.actual_result === 'void') {
      return '#cdcd00';
    } else if (appsmith.store.selectedPredictionData.actual_result === 'void-lost') {
			return '#f08080'
		} else if (appsmith.store.selectedPredictionData.actual_result === 'void-won') {
			return '#9bcd9b'
		} else {
			return 'GRAY'
		}
  }()
}}
