@import url(https://fonts.googleapis.com/css?family=Nunito+Sans:400,700);
.app {
  height: calc(var(--appsmith-ui-height) * 1px);
  width: calc(var(--appsmith-ui-width) * 1px);
  justify-content: center;
  border-radius: var(--appsmith-theme-borderRadius);
  box-shadow: var(--appsmith-theme-boxShadow);
}

div {
  color: rgba(255, 255, 255, 0.9);
}

body {
  background: #18181b;
  font-family: "Nunito Sans", sans-serif;
  width: 1000px;
  overflow-y: hidden;
}

table {
  border-collapse: collapse;
  font-weight: bold;
}

table, th, td {
  border: 0.5px solid black;
}

th {
  background-color: #726f69;
  height: 35px;
  font-size: 12px;
  line-height: 1.2;
}

td {
  background: #3a3936;
  font-size: 11px;
  height: 25px;
  line-height: 1.2;
}

td:not(.index-cellData) {
  padding-left: 5px;
}

.tableTitle {
  text-align: left;
  font-size: 12px;
  font-weight: bold;
  padding-top: 5px;
  padding-bottom: 10px;
  padding-left: 150px;
}

.index,
.index-cellData {
  width: 20px;
  text-align: center;
  position: sticky;
  left: 0px;
  z-index: 1;
}

.contest,
.Contest-cellData {
  max-width: 100px;
  position: sticky;
  left: 20px;
  z-index: 1;
}

