import React, {useState} from 'https://cdn.jsdelivr.net/npm/react@19.0.0/+esm';
import reactDom from 'https://cdn.jsdelivr.net/npm/react-dom@19.0.0/+esm';
import { createRoot } from 'https://cdn.jsdelivr.net/npm/react-dom@19.0.0/client/+esm';

class TableBuilder extends React.Component {

	 renderTableHeaders(headers, data) {

		// Do not render headers if table has no data
		if (!data || !Array.isArray(data) || data.length === 0) {
      return null;
    }

    return headers.map((headerRow, rowIndex) => (
      <tr key={rowIndex}>
        {headerRow.map((headerItem, colIndex) => {
          const { header, accessor, rowspan = 1, colspan = 1 } = headerItem;
          if (colspan > 1) {
            return (
              <th key={colIndex} colSpan={colspan} class={accessor}>
                {header}
              </th>
            );
          } else {
            return (
              <th key={colIndex} rowSpan={rowspan} class={accessor}>
                {header}
              </th>
            );
          }
        })}
      </tr>
    ));
  }

	renderTableRows(data) {

		// Do not render rows if table has no data
		if (!data || !Array.isArray(data) || data.length === 0) {
      return null;
    }

		// Saving field names to use them as class names for table fields
		const keys = Object.keys(data[0]);
		const classNames = keys.map(key => key + "-cellData");

    return data.map((row, index) => (
      <tr key={index}>
        {Object.values(row).map((cell, cellIndex) => (
          <td key={cellIndex} class={classNames[cellIndex]} dangerouslySetInnerHTML={{ __html: cell }}></td>
        ))}
      </tr>
    ));
  }

  render() {
    const { data } = this.props;
		const { headers } = this.props;
    return (
      <table style={{ width: '100%' }}>
        <thead>
					 {this.renderTableHeaders(headers, data)}
        </thead>
        <tbody>
          {this.renderTableRows(data)}
        </tbody>
      </table>
    );
  }
}

export default TableBuilder;

function App() {

	const [resultsHistory, setResultsHistory] = useState(appsmith.model.resultsHistory);

	appsmith.onModelChange(() => {

		if(resultsHistory !== appsmith.model.resultsHistory) {
			setResultsHistory(appsmith.model.resultsHistory);
		}

	});

  return (
    <div>
      <TableBuilder 
				data={resultsHistory} 
				headers={[
					[
						{ header: '', accessor: 'index', rowspan: 2 },
						{ header: 'Contest', accessor: 'contest', rowspan: 2 },
						{ header: 'Winner', accessor: 'nickname', rowspan: 2 },
						{ header: '2nd place', accessor: 'nickname', rowspan: 2 },
						{ header: '3rd place', accessor: 'nickname', rowspan: 2 },
						{ header: 'Biggest Odds', accessor: 'biggest-odds', rowspan: 2 },
						{ header: 'Streak', accessor: 'nickname', rowspan: 2 },
						{ header: 'Month 1', colspan: 3 },
						{ header: 'Month 2', colspan: 3 }
					],
					[
						{ header: 'Winner', accessor: 'nickname'},
						{ header: '2nd place', accessor: 'nickname' },
						{ header: '3rd place', accessor: 'nickname' },
						{ header: 'Winner', accessor: 'nickname'},
						{ header: '2nd place', accessor: 'nickname' },
						{ header: '3rd place', accessor: 'nickname' }
					]
				]}
			/>
			<hr></hr>
    </div>
  );
}

appsmith.onReady(() => {
  /*
   * This handler function will get called when parent application is ready.
   * Initialize your component here
   * more info - https://docs.appsmith.com/reference/widgets/custom#onready
   */
	const root = createRoot(document.getElementById('root'));
	root.render(<App />);
});
