@import url(https://fonts.googleapis.com/css?family=Nunito+Sans:400,700);
.app {
  height: calc(var(--appsmith-ui-height) * 1px);
  width: calc(var(--appsmith-ui-width) * 1px);
  justify-content: center;
  border-radius: var(--appsmith-theme-borderRadius);
  box-shadow: var(--appsmith-theme-boxShadow);
}

div {
  color: rgba(255, 255, 255, 0.9);
}

body {
  background: #18181b;
  font-family: "Nunito Sans", sans-serif;
}

table {
  border-collapse: collapse;
  font-weight: bold;
}

table, th, td {
  border: 0.5px solid black;
}

th {
  background-color: #726f69;
  height: 35px;
  font-size: 12px;
  line-height: 1.2;
}

td {
  background: #3a3936;
  font-size: 11px;
  height: 25px;
  line-height: 1.2;
}

td:not(.place-cellData, .is_active-cellData) {
  padding-left: 5px;
}

a {
  color: rgba(255, 255, 255, 0.9);
}

a:hover {
  color: rgba(209, 212, 199, 0.9);
}

.tableTitle {
  text-align: center;
  font-size: 12px;
  font-weight: bold;
  padding-top: 5px;
  padding-bottom: 10px;
}

.winning-strick {
  float: left;
  padding-right: 30px;
}

.th-nickname {
  min-width: 50px;
}

.th-contest {
  width: 140px;
}

.biggest-odds {
  width: 220px;
}

.place-cellData, .is_active-cellData {
  min-width: 15px;
  text-align: center;
}

.five-contests-points {
  max-width: 40px;
}

.container {
  display: flex;
}

.winning-streak-table {
  width: 60%;
}

.biggest-odd-table {
  width: 38%;
  margin-left: auto;
}

.strick_avg_odds {
  max-width: 40px;
}

