  text-align: center;
  font-size: 12px;
  font-weight: bold;
  padding-top: 5px;
  padding-bottom: 10px;
}

.winning-strick {
  float: left;
  padding-right: 30px;
}

.th-nickname {
  min-width: 50px;
}

.th-contest {
  width: 140px;
}

.biggest-odds {
  width: 220px;
}

.place-cellData, .is_active-cellData {
  min-width: 15px;
  text-align: center;
}

.two-contests-points {
  max-width: 40px;
}

.contest-points {
  max-width: 40px;
  overflow-wrap: normal;
}

.container {
  display: flex;
}

.winning-streak-table {
  width: 60%;
}

.biggest-odd-table {
  width: 38%;
  margin-left: auto;
}

.strick_avg_odds {
  max-width: 40px;
}

.static-rating-description {
  margin-top: 10px;
  margin-right: 10px;
  margin-left: 10px;
  text-align: justify;
  font-size: 12px;
  font-weight: bold;
  line-height: 1.5;
}

