import React, {useState} from 'https://cdn.jsdelivr.net/npm/react@18.2.0/+esm';
import reactDom from 'https://cdn.jsdelivr.net/npm/react-dom@18.2.0/+esm';

class TableBuilder extends React.Component {

	 renderTableHeaders(headers, data) {

		// Do not render headers if table has no data
		if (!data || !Array.isArray(data) || data.length === 0) {
      return null;
    }

    return headers.map((headerRow, rowIndex) => (
      <tr key={rowIndex}>
        {headerRow.map((headerItem, colIndex) => {
          const { header, accessor, rowspan = 1, colspan = 1 } = headerItem;
          if (colspan > 1) {
            return (
              <th key={colIndex} colSpan={colspan} class={accessor}>
                {header}
              </th>
            );
          } else {
            return (
              <th key={colIndex} rowSpan={rowspan} class={accessor}>
                {header}
              </th>
            );
          }
        })}
      </tr>
    ));
  }

	renderTableRows(data) {

		// Do not render rows if table has no data
		if (!data || !Array.isArray(data) || data.length === 0) {
      return null;
    }

		// Saving field names to use them as class names for table fields
		const keys = Object.keys(data[0]);
		const classNames = keys.map(key => key + "-cellData");

    return data.map((row, index) => (
      <tr key={index}>
        {Object.values(row).map((cell, cellIndex) => (
          <td key={cellIndex} class={classNames[cellIndex]} dangerouslySetInnerHTML={{ __html: cell }}></td>
        ))}
      </tr>
    ));
  }

  render() {
    const { data } = this.props;
		const { headers } = this.props;
    return (
      <table style={{ width: '100%' }}>
        <thead>
					 {this.renderTableHeaders(headers, data)}
        </thead>
        <tbody>
          {this.renderTableRows(data)}
        </tbody>
      </table>
    );
  }
}

export default TableBuilder;

function App() {

	const [crAnnualLive, setCrAnnualLive] = useState(appsmith.model.crAnnualLive);

	appsmith.onModelChange(() => {

		if(crAnnualLive !== appsmith.model.crAnnualLive) {
			setCrAnnualLive(appsmith.model.crAnnualLive);
		}

	});

  return (
    <div>
			{crAnnualLive && crAnnualLive.length > 0 && (
			 <>
			 			<hr></hr>
			 			<div class='tableTitle'>Положение с учетом мест текущего конкурса</div> 
			 </>
			 		)}
      	{crAnnualLive && crAnnualLive.length > 0 && (
		<>
			<TableBuilder 
				data={crAnnualLive} 
				headers={[
					[
						{ header: '', accessor: 'place', rowspan: 2 },
						{ header: 'Nickname', accessor: 'nickname', rowspan: 2 },
						{ header: 'Current Contest', colspan: 4 },
						{ header: '2 or 1 contests points', accessor: 'two-contests-points', rowspan: 2 },
						{ header: 'Total', accessor: 'total_points', rowspan: 2 }
					],
					[
						{ header: 'Units', accessor: 'units' },
						{ header: 'ROI', accessor: 'roi' },
						{ header: 'Place', accessor: 'place' },
						{ header: 'Points', accessor: 'points' }
					]
				]}
			/>
		</>
	)}
    </div>
  );
}

appsmith.onReady(() => {
  /*
   * This handler function will get called when parent application is ready.
   * Initialize your component here
   * more info - https://docs.appsmith.com/reference/widgets/custom#onready
   */
  reactDom.render(<App />, document.getElementById("root"));
});
