import React, {useState} from 'https://cdn.jsdelivr.net/npm/react@18.2.0/+esm';
import reactDom from 'https://cdn.jsdelivr.net/npm/react-dom@18.2.0/+esm';

class TableBuilder extends React.Component {

	 renderTableHeaders(headers, data) {

		// Do not render headers if table has no data
		if (!data || !Array.isArray(data) || data.length === 0) {
      return null;
    }

    return headers.map((headerRow, rowIndex) => (
      <tr key={rowIndex}>
        {headerRow.map((headerItem, colIndex) => {
          const { header, accessor, rowspan = 1, colspan = 1 } = headerItem;
          if (colspan > 1) {
            return (
              <th key={colIndex} colSpan={colspan} class={accessor}>
                {header}
              </th>
            );
          } else {
            return (
              <th key={colIndex} rowSpan={rowspan} class={accessor}>
                {header}
              </th>
            );
          }
        })}
      </tr>
    ));
  }

	renderTableRows(data) {

		// Do not render rows if table has no data
		if (!data || !Array.isArray(data) || data.length === 0) {
      return null;
    }

		// Saving field names to use them as class names for table fields
		const keys = Object.keys(data[0]);
		const classNames = keys.map(key => key + "-cellData");

    return data.map((row, index) => (
      <tr key={index}>
        {Object.values(row).map((cell, cellIndex) => (
          <td key={cellIndex} class={classNames[cellIndex]} dangerouslySetInnerHTML={{ __html: cell }}></td>
        ))}
      </tr>
    ));
  }

  render() {
    const { data } = this.props;
		const { headers } = this.props;
    return (
      <table style={{ width: '100%' }}>
        <thead>
					 {this.renderTableHeaders(headers, data)}
        </thead>
        <tbody>
          {this.renderTableRows(data)}
        </tbody>
      </table>
    );
  }
}

export default TableBuilder;

function App() {

	const [profitByUser, setProfitByUser] = useState(appsmith.model.profitByUser);
	const [saldo, setSaldo] = useState(appsmith.model.saldo);

	appsmith.onModelChange(() => {

		if(profitByUser !== appsmith.model.profitByUser) {
			setProfitByUser(appsmith.model.profitByUser);
		}

		if(saldo !== appsmith.model.saldo) {
			setSaldo(appsmith.model.saldo);
		}


	});

  return (
    <div>
			{profitByUser && profitByUser.length > 0 && (
			 	<>			
					<hr></hr>
			 		<div class='tableTitle'>Разница призовых и взносов</div>
			 	</>
            )}
			<TableBuilder 
				data={profitByUser} 
				headers={[
					[
						{ header: '', accessor: 'place' },
						{ header: 'Nickname', accessor: 'nickname'},
						{ header: 'Awards', accessor: 'awards' },
						{ header: 'Fees', accessor: 'fees'},
						{ header: 'Total', accessor: 'total'}
					]
				]}
			/>
			{saldo && saldo.length > 0 && (
			 	<>			
					<hr></hr>
			 		<div class='tableTitle'>Сальдо</div>
			 	</>
            )}
			<TableBuilder 
				data={saldo} 
				headers={[
					[
						{ header: '', accessor: 'place' },
						{ header: 'Nickname', accessor: 'nickname'},
						{ header: 'Profit', accessor: 'profit' },
						{ header: 'Offset', accessor: 'offset'},
						{ header: 'Saldo', accessor: 'saldo'}
					]
				]}
			/>
				<div class='saldo-table-description'>
					Эта таблица показывает кто сколько получит/потеряет, когда будем обнуляться по финансам.
			<br></br><br></br>
				- profit - разница призовых и взносов (Total из первой таблицы)
			<br></br>
				- offset - сумма, которую участник заплатил при обнулении (если с плюсом), или которую получил от других участников (если с минусом)
			<br></br>
				- saldo - то, что участник получит/заплатит при следующем обнулении
			<br></br><br></br>
				Обе таблицы не учитывают призовые и взносы конкурса, который еще не завершен
			</div>
    </div>
  );
}

appsmith.onReady(() => {
  /*
   * This handler function will get called when parent application is ready.
   * Initialize your component here
   * more info - https://docs.appsmith.com/reference/widgets/custom#onready
   */
  reactDom.render(<App />, document.getElementById("root"));
});
